import React from "react";
import { Link, json, defer } from "react-router-dom";

const ShoppingBagPage = () => {
  return (
    <div>
      <div>
        <main className="grid min-h-full place-items-center bg-white px-6 py-24 sm:py-32 lg:px-8">
          <div className="text-center">
            <h1 className="mt-4 text-3xl font-bold tracking-tight text-gray-900 sm:text-5xl">
              No products in bag
            </h1>
            <p className="mt-6 text-base leading-7 text-gray-600">
              Add some products in your bag to start shopping
            </p>
            <div className="mt-10 flex items-center justify-center gap-x-6">
              <Link
                to="/products"
                className="text-sm font-semibold text-gray-900"
              >
                Start shopping <span aria-hidden="true">&rarr;</span>
              </Link>
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default ShoppingBagPage;

async function loadShoppingBag() {
  const response = await fetch("http://localhost:5000/shoppingBag/");

  if (!response.ok) {
    throw json(
      { message: "Unable to fetch shopping bag data" },
      { status: 500 }
    );
  } else {
    const shoppingBag = await response.json();
    return shoppingBag;
  }
}

export function loader() {
  return defer({
    shoppingBag: loadShoppingBag(),
  });
}
