import React from "react";
import { useState } from "react";

const addProduct = async (titleValue) => {
  const product = await fetch("http://localhost:5000/admin/add-product", {
    method: "POST",
    headers: { "Content-Type": "application/json" },
    body: JSON.stringify({
      title: titleValue,
    }),
  });
  console.log(product);
};

const AddProduct = () => {
  const [titleValue, setTitleValue] = useState("");
  // const [descriptionValue, setDescriptionValue] = useState("");
  // const [colorValue, setColorValue] = useState("");
  // const [categoryValue, setCategoryValue] = useState("");
  // const [priceValue, setPriceValue] = useState("");
  // const [imageValue, setImageValue] = useState("");
  const [productDetails, setProductDetails] = useState({
    title: "",
    description: "",
    category: "",
    color: "",
    price: "",
    image: "",
  });

  const inputChangeHandler = (e) => {
    const { name, value } = e.target;
    setProductDetails((prevStateDetails) => ({
      ...prevStateDetails,
      [name]: value,
    }));
    console.log(productDetails);
  };

  const imageChangeHandler = (e) => {};

  const submitHandler = (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("title", productDetails.title);
    formData.append("description", productDetails.description);
    formData.append("category", productDetails.category);
    formData.append("color", productDetails.color);
    formData.append("price", productDetails.price);
    formData.append("image", productDetails.image); // Append the image file

    addProduct(formData); // Send the FormData object to the server

    // Optionally reset form fields after submission
    setProductDetails({
      title: "",
      description: "",
      category: "",
      color: "",
      price: "",
      image: null, // Reset image state to null
    });
  };

  return (
    <div className="flex min-h-full flex-1 flex-col justify-center px-6 py-12 lg:px-8">
      <div className="sm:mx-auto sm:w-full sm:max-w-sm">
        <h2 className="mt-10 text-center text-2xl font-bold leading-9 tracking-tight text-gray-900">
          Add New Product
        </h2>
      </div>

      <div className="mt-10 sm:mx-auto sm:w-full sm:max-w-sm">
        <form onSubmit={submitHandler} className="space-y-6">
          <div>
            <label
              htmlFor="title"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Title
            </label>
            <div className="mt-2">
              <input
                id="title"
                name="title"
                type="text"
                required
                onChange={inputChangeHandler}
                value={productDetails.title}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <div>
            <label
              htmlFor="description"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Description
            </label>
            <div className="mt-2">
              <input
                id="description"
                name="description"
                type="text"
                required
                onChange={inputChangeHandler}
                value={productDetails.description}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <div>
            <label
              htmlFor="category"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Category
            </label>
            <div className="mt-2">
              <input
                id="category"
                name="category"
                type="text"
                required
                onChange={inputChangeHandler}
                value={productDetails.category}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <div>
            <label
              htmlFor="color"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Color
            </label>
            <div className="mt-2">
              <input
                id="color"
                name="color"
                type="text"
                required
                onChange={inputChangeHandler}
                value={productDetails.color}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <div>
            <label
              htmlFor="price"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Price
            </label>
            <div className="mt-2">
              <input
                id="price"
                name="price"
                type="number"
                required
                onChange={inputChangeHandler}
                value={productDetails.price}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>
          <div>
            <label
              htmlFor="image"
              className="block text-sm font-medium leading-6 text-gray-900"
            >
              Image
            </label>
            <div className="mt-2">
              <input
                id="image"
                name="image"
                type="file"
                required
                onChange={imageChangeHandler}
                value={productDetails.image}
                className="block w-full rounded-md border-0 py-1.5 text-gray-900 shadow-sm ring-1 ring-inset ring-gray-300 placeholder:text-gray-400 focus:ring-2 focus:ring-inset focus:ring-indigo-600 sm:text-sm sm:leading-6"
              />
            </div>
          </div>

          <div>
            <button
              type="submit"
              className="flex w-full justify-center rounded-md bg-indigo-600 px-3 py-1.5 text-sm font-semibold leading-6 text-white shadow-sm hover:bg-indigo-500 focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-indigo-600"
            >
              Add product
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddProduct;
