import React, { Suspense } from "react";
import { Await, defer, json, useLoaderData } from "react-router-dom";
import OrdersOverview from "../components/OrdersView";

const OrdersPage = () => {
  const orders = useLoaderData();

  return (
    <div>
      <Suspense fallback={<p style={{ textAlign: "center" }}>Loading...</p>}>
        <Await resolve={orders}>
          {(loadOrders) => {
            <OrdersOverview orders={loadOrders} />;
          }}
        </Await>
      </Suspense>
      <div className="w-11/12 mx-auto">
        <h1 className="text-3xl font-bold text-gray-900 mt-3">Your Orders</h1>
        <ul className="mx-auto border border-slate-300 rounded-md mt-4">
          <li className="w-11/12 mx-auto my-6">
            <h2 className="text-2xl font-semibold text-gray-800 mb-3 text-left pl-4 pt-2">
              Order - #
            </h2>
            <ul className="divide-y divide-gray-100 border border-slate-300 rounded-md mx-auto">
              <li className="flex justify-between gap-x-6 py-5 w-11/12 mx-auto">
                <div className="flex min-w-0 gap-x-4">
                  <img
                    className="h-12 w-12 flex-none rounded-full bg-gray-50"
                    src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQBT4I8RhE9aScdcEzd54w7ZtOh0wXHEKr9rQ&s"
                    alt=""
                  />
                  <div className="min-w-0 flex-auto">
                    <p className="text-sm font-semibold leading-6 text-gray-900">
                      Product Title
                    </p>
                    <p className="mt-1 truncate text-xs leading-5 text-gray-500">
                      Product description
                    </p>
                  </div>
                </div>
                <div className="hidden shrink-0 sm:flex sm:flex-col sm:items-end">
                  <p className="text-sm leading-6 text-gray-900">Rs. 500</p>
                  <p className="mt-1 text-xs leading-5 text-gray-500">
                    Qty x 1
                  </p>
                </div>
              </li>
              <div className="flex flex-row justify-between p-4">
                <p className="mt-1 text-sm leading-5 text-gray-800 pl-6">
                  Order Amount
                </p>
                <p className="mt-1 text-sm leading-5 text-black font-bold pr-12">
                  Rs. 500
                </p>
              </div>
              <p className="mt-1 text-sm leading-5 text-gray-800 text-right p-4">
                Placed at{" "}
                <time dateTime="2023-01-23T13:23Z">{`${new Date()}`}</time>
              </p>
            </ul>
          </li>
        </ul>
      </div>
    </div>
  );
};

export default OrdersPage;

async function loadOrders() {
  const response = await fetch("http://localhost:5000/orders/");

  if (!response.ok) {
    throw json({ message: "Unable to fetch orders" }, { status: 500 });
  } else {
    const orders = await response.json();
    console.log(orders);
    return orders;
  }
}

export function loader() {
  return defer({
    orders: loadOrders(),
  });
}
