import React, { Suspense } from "react";
import { json, defer, useLoaderData, Await } from "react-router-dom";
import ShoppingBagOverview from "../components/ShoppingBagOverview";

const ShoppingBagPage = () => {
  const cart = useLoaderData(loadShoppingBag);

  return (
    <div>
      {/* <NoProductsBag /> */}
      <Suspense fallback={<p style={{ textAlign: "center" }}>Loading...</p>}>
        <Await resolve={cart}>
          {(loadShoppingBag) => <ShoppingBagOverview cart={loadShoppingBag} />}
        </Await>
      </Suspense>
    </div>
  );
};

export default ShoppingBagPage;

async function loadShoppingBag() {
  const response = await fetch("http://localhost:5000/shoppingBag/");

  if (!response.ok) {
    throw json(
      { message: "Unable to fetch shopping bag data" },
      { status: 500 }
    );
  } else {
    const shoppingBag = await response.json();
    return shoppingBag;
  }
}

export function loader() {
  return defer({
    shoppingBag: loadShoppingBag(),
  });
}
