import React, {
  Suspense,
  useContext,
  useEffect,
  useCallback,
  useState,
} from "react";
import {
  useLocation,
  Await,
  defer,
  useRouteError,
  useAsyncError,
} from "react-router-dom";
import { useLoaderData } from "react-router-dom";
import ProductList from "../components/ProductList";
import AuthContext from "../context/auth";
import ErrorPage from "./ErrorPage";

const ProductsPage = () => {
  const location = useLocation();
  const adminPage = location.pathname === "/admin/products";
  const { products } = useLoaderData();
  const authCtx = useContext(AuthContext);
  const [hasLoggedIn, setHasLoggedIn] = useState(false);
  // const error = useRouteError();
  const asyncError = useAsyncError();

  const loginUser = useCallback(
    (username, roleAdmin) => {
      authCtx.login(username, roleAdmin);
      setHasLoggedIn(true);
    },
    [authCtx]
  );

  useEffect(() => {
    const userInfo = sessionStorage.getItem("user");
    if (userInfo && !hasLoggedIn) {
      const parsedUser = JSON.parse(userInfo);
      loginUser(parsedUser.username, parsedUser.roleAdmin);
    }
  }, [loginUser, hasLoggedIn]);

  // if (error) {
  //   return <ErrorPage status={error.status} />;
  // }

  return (
    <div className="bg-white">
      <div className="mx-auto max-w-2xl px-4 py-16 sm:px-6 sm:py-24 lg:max-w-7xl lg:px-8">
        <h2 className="text-2xl font-bold tracking-tight text-gray-900">
          Trends for this year
        </h2>
        <Suspense fallback={<p style={{ textAlign: "center" }}>Loading...</p>}>
          <Await
            resolve={products}
            errorElement={<div>Could not load reviews ðŸ˜¬ </div>}
          >
            {(loadProducts) => (
              <ProductList products={loadProducts} adminPage={adminPage} />
            )}
          </Await>
        </Suspense>
      </div>
    </div>
  );
};

export default ProductsPage;

async function loadProducts() {
  const isAdmin = window.location.pathname.startsWith("/admin");

  const response = await fetch(
    "http://localhost:5000/" + (isAdmin ? "admin/products/" : "products/"),
    {
      credentials: "include",
    }
  );

  if (!response.ok) {
    throw new Response("Data not found", { status: response.status });
  }

  const products = await response.json();

  sessionStorage.setItem("products", JSON.stringify(products));
  return products;
}

export function loader() {
  return defer({
    products: loadProducts(),
  });
}
